#
# Structure for table "person"
#

DROP TABLE IF EXISTS `person`;
CREATE TABLE `person` (
  `Id` varchar(50) NOT NULL DEFAULT '',
  `name` varchar(100) NOT NULL DEFAULT '' COMMENT '姓名',
  `password` varchar(50) NOT NULL DEFAULT '' COMMENT '密码',
  `role` varchar(10) NOT NULL DEFAULT '' COMMENT '身份',
  `age` int(11) DEFAULT NULL COMMENT '年龄',
  `education` varchar(50) DEFAULT NULL COMMENT '学历',
  `phone_num` varchar(50) NOT NULL DEFAULT '' COMMENT '联系电话',
  `identity_num` varchar(50) DEFAULT NULL COMMENT '身份证号',
  `address` varchar(100) DEFAULT NULL COMMENT '通讯地址',
  `introduce` tinytext COMMENT '个人简介',
  `check_time` timestamp NULL DEFAULT NULL COMMENT '审核时间',
  `checkman_id` varchar(50) DEFAULT NULL COMMENT '审核人ID',
  `is_passed` int(11) DEFAULT NULL COMMENT '是否通过',
  `upd_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `upgrade_level` varchar(10) DEFAULT NULL COMMENT '升级职位',
  `upgrade_reason` text COMMENT '升级理由',
  `is_ucheck_pass` int(11) DEFAULT NULL COMMENT '升级是否通过',
  `ucheckman_id` varchar(50) DEFAULT NULL COMMENT '升级审核人',
  `ucheck_comment` text COMMENT '升级审核意见',
  `ucheck_time` timestamp NULL DEFAULT NULL COMMENT '升级审核时间',
  `del_flag` int(11) NOT NULL DEFAULT '0',
  `avatar` tinytext COMMENT '头像路径',
  PRIMARY KEY (`Id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='人员';

